/** FILTERS **/
appAccelaar.filter('company_type', function(){
  return function(input){
    if(!input) return null
    var newInput = input.replace('_',' ');
    return newInput.charAt(0).toUpperCase()+newInput.substr(1).toLowerCase()
  }
});

/** END FILTER **/
